"use strict";
var socket = io('http://localhost:8080', { query: 'authType=javascript' });

socket.on('connect', function(data) {
    console.log(data);
});

// escuchar
socket.on('disconnect', function() {
    console.log('Perdimos conexión con el servidor');
});

let GetRoutingId = function(HTML_CONTENT) {
    let bind = HTML_CONTENT[0].attributes[1].nodeValue;
    // Enviamos por conexión bidimensional los datos del tracking
    socket.emit('routing', {
        serviceId: bind
    }, function(NodeServerResp) {
        console.log(NodeServerResp)
    });
    socket.on('TrackingData', function(data) {
        // console.log(data);
    });
}